const codes=document.querySelectorAll(".homeListCode"),buttonsArr=document.querySelectorAll(".homeListBtn");let buttonsOnn=new Map,buttonsOff=new Map;function addButtonsOnn(){for(let t=0;t<buttonsArr.length;t++)buttonsOnn.set(t,buttonsArr[t])}function homeCodeOnnbtn(){const t=buttonsOnn.keys();for(let n of t){const t=buttonsOnn.get(n);t.addEventListener("click",(()=>{codes[n].classList.add("homeListCodeActive"),t.innerHTML="Скрыть JS код",buttonsOff.set(n,t),buttonsOnn.delete(n)}))}}function homeCodeOffbtn(){const t=buttonsOff.keys();for(let n of t){const t=buttonsOff.get(n);t.addEventListener("click",(()=>{codes[n].classList.remove("homeListCodeActive"),t.innerHTML="Открыть JS код",buttonsOnn.set(n,t),buttonsOff.delete(n)}))}}addButtonsOnn(),homeCodeOnnbtn(),homeCodeOffbtn();